const mobileBreakpoint=768,hamburger=document.querySelector(".hamburger"),navLinks=document.querySelector(".nav-links"),navLinksAnchors=document.querySelectorAll(".nav-links a");hamburger.addEventListener("click",()=>{hamburger.classList.toggle("active"),navLinks.classList.toggle("active")}),navLinksAnchors.forEach(e=>{e.addEventListener("click",()=>{hamburger.classList.remove("active"),navLinks.classList.remove("active")})});const navItems=document.querySelectorAll(".nav-item");navItems.forEach(e=>{e.addEventListener("click",t=>{if(window.innerWidth<=mobileBreakpoint){if(!e.querySelector(".dropdown"))return;navItems.forEach(t=>{t!==e&&t.classList.remove("active")}),e.querySelector(".dropdown")&&e.classList.toggle("active")}})}),document.addEventListener("click",e=>{if(window.innerWidth>mobileBreakpoint)return;const t=document.querySelector(".nav-item.active");t&&!t.contains(e.target)&&t.classList.remove("active")})